INSERT INTO `books` (`id`, `title`, `author`, `description`, `pages`, `price`, `image_url`, `likes`) VALUES
	(19, 'Fortuna\'s kinderen', 'Annejet van der Zijl', 'Als Leon Herckenrath zich op 22 oktober 1818 inscheept op de Washington, kan hij onmogelijk vermoeden welke toekomst hem wacht in Amerika. Het schip voert Leon naar Charleston, een stad die bekendstaat om haar rijkdom en schoonheid. De jonge Hollander ontdekt echter dat het zuidelijke Charleston ook een andere kant heeft: die van ouderwets en wreed centrum van slavernij. Door een speling van het lot en vooral dankzij zijn eigen moed en doorzettingsvermogen, wordt hij een succesvolle speler in het negentiende-eeuwse zakenleven.  Maar wat Leon meer dan alles aan de ambivalente nieuwe natie zal binden, is zijn verboden en gevaarlijke liefdesrelatie met de als slavin geboren Juliette. Ook de kinderen en kleinkinderen van Leon en Juliette komen in een bijzondere setting ter wereld. Een deel van hen grijpt de kansen die Vrouwe Fortuna biedt net zo dapper en succesvol aan als Leon deed. Deze verhalen blijven bijzonder: van overleven in een brandend San Francisco tot baden in Zwitserse luxe. Terugkerende drijfveren van de personages zijn familie, rechtvaardigheidsgevoel, loyaliteit en liefde.', 287, 12, 'https://www.jeugdbibliotheek.nl/content/dam/landelijk/jeugdbibliotheek/lezen-voor-de-lijst/15-18-jaar/niveau3/fortunas-kinderen.jpg.rendition.200.320.jpeg', 0),
	(20, 'Vluchthaven', 'Anne van den Dool', 'Als haar stiefopa overlijdt, reist Hannah naar zijn geboorteland Indonesië om zijn as uit te strooien. Op het moment dat ze de vluchthaven verlaat, slaat de onzekerheid toe: terwijl ze angstvallig de grote envelop met as in haar rugzak in de gaten houdt, voelt ze zich niet thuis tussen de toeristen, maar ook niet tussen de lokale bevolking. Nergens kan ze een plek vinden die goed voelt om de as uit te strooien.  Vol afgrijzen beschouwt Hannah de constante stroom van toeristen die het prachtige Indonesië vervuilt. Ze wil haar stiefopa begrijpen, zijn verleden en zijn trauma’s uit het jappenkamp, terwijl ze zich tegelijkertijd schuldig voelt omdat zij geen bloedverwant is van hem. Op zoek naar wie haar opa was, vraagt ze zich steeds meer af wie zij eigenlijk zelf is.', 304, 12, 'https://www.jeugdbibliotheek.nl/content/dam/landelijk/jeugdbibliotheek/lezen-voor-de-lijst/15-18-jaar/niveau3/vluchthaven.jpg.rendition.384.614.jpeg', 0),
	(21, 'Zomervacht', 'Jaap Robben', 'De dertienjarige Brian woont samen met zijn vader Maurice op een afgelegen terrein in een vervallen caravan. Niet de ideale plek om Brians verstandelijk en fysiek beperkte broer Lucien op te vangen als de instelling waar hij woont wordt verbouwd. Toch ziet Maurice hier wel de voordelen van in als blijkt dat er een vergoeding tegenover staat. Dat geld kunnen ze goed gebruiken en hij heeft ‘Brai’ toch om voor Lucien te zorgen?  Maar hoe doe je dat, zorgen voor iemand die niet kan praten? Ondertussen is er ook nog Selma, het meisje dat Brian heeft leren kennen in de instelling van Lucien, en met wie hij dolgraag een ritje maakt op zijn nieuwe brommer. Gelukkig is er net een nieuwe huurder gearriveerd in de leegstaande caravan verderop op het terrein. Emile, bij wie het thuis met zijn vriendin ‘even niet zo lekker loopt’, houdt wel een oogje in het zeil. Maar is deze sympathieke man eigenlijk wel te vertrouwen? En lukt het Brian dan toch een band met zijn broer op te bouwen of groeit de zorg hem boven het hoofd?', 316, 12, 'https://www.jeugdbibliotheek.nl/content/dam/landelijk/jeugdbibliotheek/lezen-voor-de-lijst/15-18-jaar/niveau3/zomervacht.jpg.rendition.384.614.jpeg', 1),
	(22, 'Het jongensuur', 'Andreas Burnier', '‘Het jongensuur’ beschrijft, in de ik-vorm, de ervaringen van het joodse meisje Simone tijdens de oorlog. In 1940, als zij 9 jaar is, krijgt ze te horen dat zij alleen, gescheiden van haar ouders, moet onderduiken. Door de jaren heen gaat ze van onderduikadres naar onderduikadres. Telkens weer moet ze zich aanpassen aan een nieuwe omgeving en bijna overal blijft ze een buitenstaander.  Daarnaast verzet ze zich tegen haar steeds duidelijker wordende vrouw-zijn: ze wil een jongen zijn en weigert voor te sorteren op de rol die een vrouw kennelijk moet vervullen. De werkelijkheid biedt haar nauwelijks ruimte, maar haar fantasie kent geen grenzen. ', 89, 12, 'https://www.jeugdbibliotheek.nl/content/dam/landelijk/jeugdbibliotheek/lezen-voor-de-lijst/15-18-jaar/niveau3/het-jongensuur.jpg.rendition.384.614.jpeg', 0),
	(23, 'Confrontaties', 'Simone Atangana Bekono', 'De zestienjarige Salomé Atabong zit vast in een jeugddetentiecentrum vanwege ernstige mishandeling van twee schoolgenoten. Ze probeert zich staande te houden tussen de andere meiden van het centrum, en de begeleiders en psychologen, met wie ze behoorlijk botst. Over een maand mag ze naar huis, waar een gezin wacht dat al lang geleden uit elkaar is gevallen.  Je leest vanuit Salomé hoe zij haar verblijf in het centrum ervaart, hoe zij haar positie bepaalt ten opzichte van andere mensen. Ze heeft regelmatig last van woedeaanvallen, maar ook van grote onzekerheid en angstdromen: ‘Net op het moment dat hun klauwen zich in mijn benen zetten val ik de diepte in, alsof de grond zich voor me opent, alsof de onderwereld mijn vlees wil. Ik blijf maar vallen, maar het is niet genoeg.’ Haar ervaringen nu worden afgewisseld met terugblikken en maken nieuwsgierig naar wat er precies gebeurd is voordat zij kwam vast te zitten.', 224, 12, 'https://b2.gigafyde.net/file/gify-file/2022/04/01/1f2b00c2-f531-4ada-9504-3f46d49def76.jpg', 0),
	(24, 'Viktor', 'Judith Fanto', 'De kritische studente Geertje van den Berg is op zoek naar haar identiteit. Ze verzet zich openlijk tegen de angst en schaamte die haar familie sinds de oorlog beheerst met betrekking tot hun Joodse afkomst. Tijdens de zoektocht naar haar Joodse familiegeschiedenis in Wenen ten tijde van de Tweede Wereldoorlog ontdekt ze een onthutsend familiegeheim.  Dat geheim heeft alles te maken met een al even rebelse oud-oom van Geertje: Viktor Rosenbaum. Hij is het ‘zwarte schaap’ van de gegoede Weens-Joodse familie: hij maakt geen studies af, komt altijd te laat op afspraakjes met vrouwen, met wie hij het na drie afspraakjes steevast uitmaakt, en komt op allerlei niet-alledaagse manieren aan geld. De familie weet zich geen raad met dit ‘enfant terrible’. In 1938 lijft Adolf Hitler Oostenrijk in; de familie krijgt steeds openlijker te maken met anti-semitisme en verkeert in levensgevaar. Viktor werpt zich echter op met een gewaagd plan om zijn familie te redden uit handen van de Duitsers.', 397, 12, 'https://www.jeugdbibliotheek.nl/content/dam/landelijk/jeugdbibliotheek/lezen-voor-de-lijst/15-18-jaar/niveau3/viktor.png.rendition.384.614.png', 0),
	(25, 'De geschiedenis van mijn seksualiteit', 'Tobi Lakmaker', '‘Zo rond mijn zeventiende is bij mij het idee ontstaan om een genie te worden. Het vervelende van genialiteit is alleen dat het net zoiets is als homoseksualiteit: je wordt het niet, je blijkt het te zijn. Althans dat zeggen ze.’ Welkom in de wereld van Sofie Lakmaker. In \'De geschiedenis van mijn seksualiteit\' maakt Sofie je deelgenoot van haar onbestemde en woelige bestaan, waarin zij wil weten wie ze is, op wie ze valt en bij wie ze zich echt thuis voelt.  Sofie viel namelijk eerst op mannen en toen op vrouwen, en zit sinds kort in een praatgroep voor transgenders, maar wil eigenlijk vooral ‘iets meer jongen worden’. Van flirten op het voetbalveld naar filosoferen op de Universiteit van Amsterdam: waar vindt ze antwoorden, of is de juiste vraag niet gesteld? En dan blijkt daarnaast haar moeder ernstig ziek. De geschiedenis van mijn seksualiteit is het debuut van - voortaan - Tobi Lakmaker.', 223, 12, 'https://www.jeugdbibliotheek.nl/content/dam/landelijk/jeugdbibliotheek/lezen-voor-de-lijst/15-18-jaar/niveau3/lakmaker.jpg.rendition.384.614.jpeg', 0),
	(26, 'De opgang', 'Stefan Hertmans', 'Als schrijver Stefan Hertmans in 1979 een huis in de Gentse wijk Patershol betrekt, weet hij nog niet dat het huis ooit bewoond werd door een Vlaamse SS’er. Pas nadat hij twintig jaar later het huis heeft verkocht, komt hij er bij toeval achter en gaat hij onderzoek doen naar deze vorige bewoner. Wie was deze Willem Verhulst en hoe konden zijn vredelievende vrouw Mientje en hun kinderen samen met hem onder één dak leven?  Hertmans wil graag vat krijgen op Willem Verhulst en zijn familie. Daarvoor duikt hij de archieven in, spreekt nabestaanden, leest brieven en gaat in zijn herinnering alle kamers langs van het huis waar hij zelf jaren heeft gewoond. Stukje bij beetje ontrafelt zich niet alleen een politiek drama, maar ook een huwelijksdrama.', 352, 12, 'https://www.jeugdbibliotheek.nl/content/dam/landelijk/jeugdbibliotheek/lezen-voor-de-lijst/15-18-jaar/niveau4/de-opgang.jpg.rendition.384.614.jpeg', 0),
	(27, 'Zuurstofschuld', 'Toine Heijmans', 'De 19-jarige Walter Welzenbach is net begonnen met zijn studie wiskunde als hij de iets oudere Lenny Tichy ontmoet, een lange jongen met een knalrode hanenkam die bezig is een brugpijler te beklimmen. Dat is het begin van Walters inwijding in de klimsport. De twee worden klimvrienden; samen bouwen ze een bestaan op dat helemaal bepaald wordt door het alpinisme, eerst in de Franse Alpen, later in de Himalaya.  Tijdens een van hun klimtochten vertelt Lenny hem opeens dat zijn vriendin zwanger is. Daarom gaat hij stoppen met klimmen, hij scheert zijn hanenkam af en begint een gewoon burgerlijk leven in een Nederlandse Vinexwijk. Walter is nog lang niet klaar met klimmen. Maar hoe lang kun je ermee doorgaan? En wanneer is je doel - welk doel? - bereikt?', 336, 12, 'https://www.jeugdbibliotheek.nl/content/dam/landelijk/jeugdbibliotheek/lezen-voor-de-lijst/15-18-jaar/niveau4/zuurstofschuld.jpg.rendition.384.614.jpeg', 0);
